<script lang="ts">
	import html2canvas from 'html2canvas';

	let { data } = $props();
	let { tracks, playlist } = $derived(data);
	let form: HTMLFormElement;
	let buttonText = $state('Generate Share Images');

	async function screenshot() {
		buttonText = 'Generating...';
		let promise = Promise.resolve();
		Array.from(document.querySelectorAll<HTMLDivElement>('.track-container')).forEach(
			(container) => {
				promise = promise.then(() => {
					return html2canvas(container).then((canvas) => {
						const input = container.querySelector<HTMLInputElement>('input');
						if (!input) {
							console.error('no input found for', container);
							return;
						}
						input.value = canvas.toDataURL();
					});
				});
			}
		);

		await promise;
		form.submit();

		buttonText = 'Done';
	}
</script>

<form class="prose" bind:this={form} method="POST">
	{#each tracks as t}
		{@const track = t.metadata}
		<div style:width="1200px" style:height="630px" class="flex px-8 track-container my-4 relative">
			<input type="hidden" name={`${playlist.slug}|${track.id}`} />
			<div class="w-1/2 p-8 m-0 flex flex-col justify-center">
				<div class="text-5xl flex">
					<h1 class="pr-4 my-0 text-5xl">{track.number}.</h1>
					<div>
						<h1 class="my-0 text-5xl">
							{track.name}
							<span class="text-gray-700 text-3xl text-nowrap">by {track.artist}</span>
						</h1>
					</div>
				</div>
				<blockquote class=" flex justify-center flex-col text-2xl text-ellipsis flex-grow">
					{#each track.lines as line}
						{#if line.share}
							<div class:my-4={line.spacer}>{line.words}</div>
						{/if}
					{/each}
				</blockquote>

				<h2 class="text-2xl text-gray-600 mt-2 mb-0">
					bladebarringer.com/playlists/{playlist.slug}
				</h2>
			</div>
			<img class="w-1/2" src={track.img} alt="" />
		</div>
	{/each}
</form>

<div class="text-center p-4">
	<button class="px-8 py-2 text-2xl rounded bg-white" onclick={screenshot}>{buttonText}</button>
</div>

